mapper.update:
UPDATE reviewNote
SET reviewNoteTitle = #{reviewNoteTitle},
reviewNote = #{reviewNote}
WHERE reviewNoteId = #{reviewNoteId}
AND userId = #{userId}

UPDATE reviewNote
SET reviewPageViewState = 'DELETE'
WHERE reviewNoteId = #{reviewNoteId}
AND userId = #{userId}

UPDATE reviewNote
SET reviewNoteCommentNum = reviewNoteCommentNum + 1
WHERE reviewNoteId = #{reviewNoteId}

UPDATE reviewNote
SET reviewNoteCommentNum = reviewNoteCommentNum - 1
WHERE reviewNoteId = #{reviewNoteId}

mapper.select:
SELECT count(*)
FROM reviewNoteLike
WHERE reviewNoteId = #{reviewNoteId}
AND userId = #{userId};

mapper.update:
UPDATE reviewNote
SET reviewNoteLike = reviewNoteLike + 1
WHERE reviewNoteId = #{reviewNoteId}

UPDATE reviewNote
SET reviewNoteLike = reviewNoteLike - 1
WHERE reviewNoteId = #{reviewNoteId}

mapper.delete:
DELETE 
FROM reviewNoteLike
WHERE reviewNoteId = #{reviewNoteId}
AND userId = #{userId};
